hudson.tasks.Shell.command:
curl -X POST http://docker.host:4243/containers/planets-mysql-db/stop

curl -X DELETE http://docker.host:4243/containers/planets-mysql-db

curl -X POST -H "Content-Type: application/json" -d '{
  "Hostname": "planets.db",
  "Image": "mysql:5.7",
  "Env": [
    "MYSQL_ROOT_PASSWORD=mysql",
    "MYSQL_USER=bookstore",
    "MYSQL_PASSWORD=1bookstore!",
    "MYSQL_DATABASE=bookstoredb"
  ],
  "ExposedPorts": {
    "3306/tcp": {}
  }
}' http://docker.host:4243/containers/create?name=planets-mysql-db

curl -v -X POST -H "Content-Type: application/json" -d '{
  "PortBindings": {
    "3306/tcp": [{
      "HostPort": "3306"
    }]
  },
  "RestartPolicy": {
    "Name": "always"
  }
}' http://docker.host:4243/containers/planets-mysql-db/start

sleep 10

mvn -s /opt/maven_config/.m2/settings.xml clean compile flyway:migrate -Ddatabase.host=docker.host

wget "http://nexus:8081/service/local/artifact/maven/redirect?r=snapshots&g=org.terra&a=planet&v=LATEST&e=war" -O $WORKSPACE/continuous-delivery/jenkins/docker/bookstore.war

tar zcf $WORKSPACE/continuous-delivery/Dockerfile.tar.gz -C $WORKSPACE/continuous-delivery/jenkins/docker/ .

curl -X POST -H "Content-Type: application/tar" --data-binary @$WORKSPACE/continuous-delivery/Dockerfile.tar.gz http://docker.host:4243/build?t=planets-web:$BUILD_NUMBER

rm $WORKSPACE/continuous-delivery/Dockerfile.tar.gz

curl -X POST http://docker.host:4243/containers/planets-mysql-db/stop

curl -X DELETE http://docker.host:4243/containers/planets-mysql-db
